<template>
  <div class="wrapper">
    <mip-fixed type="top">
      <div class="t-nav">
        <div class="data">
          <div
            v-for="(t,index) in timeary"
            :key="t"
            :class="{atv:inds == index}"
            class="item"
            @touchend="getime(index)">{{ t.week }}
            <div
              class="datanum"
              v-text="t.day"/>
          </div>
        </div>
      </div>
    </mip-fixed>
    <div class="t-box">
      <div
        v-for="(m,index) in timeary[tab].times"
        :key="m"
        :class="{man:m.busy,activity:dis == index }"
        class="time"
        @touchend="getli(index)">{{ m.time }}</div>
    </div>
    <mip-fixed
      class="fixbotm"
      type="bottom">
      <div class="t-footer">
        <div class="beizhu">实际到达时间可能会有30分钟的浮动</div>
        <button
          class="t-btn"
          @touchend="sure()">确定选择</button>
      </div>
    </mip-fixed>
    <div
      v-show="warn.show"
      class="layer">
      <div class="layer-content zoomIn">
        <p
          class="layer-text"
          v-text="warn.texts"/>
        <p
          class="layer-sure active-layer"
          @touchend="closeLayer">知道了</p>
      </div>
    </div>

  </div>
</template>

<script>
import base from '../../common/utils/base'
import '../../common/utils/base.less'
export default {
  data () {
    return {
      parm: JSON.parse(decodeURIComponent(base.getRequest(location.href).parm)),
      warn: {
        // 弹窗
        show: false,
        texts: ''
      },
      inds: 0,
      timeary: [],
      tab: 0,
      dis: null,
      channel: 'baidu',
      times: {},
      useradd: ''
    }
  },
  mounted () {
    let useradd = sessionStorage.getItem('useradd')
    if (useradd) {
      this.useradd = useradd
    }
    this.timelist()
  },
  methods: {
    timelist () {
      let that = this
      let parm = that.parm
      let position = base.getposition()
      let communityId = position.id || position.communityId
      let url = '/daoway/rest/service/' + parm.serviceId + '/appointable_times?includeBusyFlag=true&channel=' + that.channel + '&manualCity=' + encodeURIComponent(position.city) + '&lot=' + position.lot + '&lat=' + position.lat + '&communityId=' + communityId
      if (parm.street) {
        url += '&street=' + encodeURIComponent(parm.street)
      }
      if (parm.house) {
        url += '&house=' + encodeURIComponent(parm.house)
      }
      if (parm.priceId) {
        url += '&priceId=' + parm.priceId
      }
      if (parm.quantity) {
        url += '&quantity=' + parm.quantity
      }
      fetch(url, {
        method: 'get'
      }).then(function (res) {
        return res.json()
      }).then(function (text) {
        let datas = text.data
        for (let i = 0, len = datas.length; i < len; i++) {
          let data = datas[i]
          data.week = base.timeformat(data.date, 'day')
          data.day = base.timeformat(data.date, 'MM-dd')
          that.timeary.push(data)
        }
      }).catch(function (error) {
        console.log(error)
      })
    },
    getime (index) {
      let that = this
      that.inds = index
      that.tab = index
    },
    getli (index) {
      this.dis = index
    },
    sure () {
      let that = this
      let tab = that.timeary[that.tab]
      let dis = tab.times[that.dis].time
      let date = base.timeformat(tab.date, 'yyyy/MM/dd')
      let appointTime = new Date(date + ' ' + dis).getTime()
      sessionStorage.setItem('apptime', appointTime)
      sessionStorage.setItem('useradd', this.useradd)
      MIP.viewer.page.back()
    }
  }
}
</script>

<style scoped>
.wrapper {
  margin: 0 auto;
  text-align: center;
}

.t-nav {
  width: 100%;
  height: 60px;
  background: #fff;
  overflow-x: scroll;
  margin-top: 44px;
  border-top: 1px solid #f5f5f5;
}

.data {
  width: 3400px;
}

.item {
  width:45px;
  margin: 0 5px;
  text-align: center;
  height: 55px;
  line-height: 20px;
  float: left;
  padding: 6px 0 0;
}

.atv {
  border-bottom: 2px solid #f34e55;
  color: #f34e55;
}

.datanum {
  font-size: 14px;
}

.t-box {
  margin: 70px 0 auto 0;
  width:100%;
  height: 80%;
  overflow-y: scroll;
  text-align: left;
}
.fixbotm{
  bottom: 10px;
}

.time {
  width: 21%;
  display: inline-block;
  text-align: center;
  height: 33px;
  line-height: 33px;
  border: 1px solid #ececec;
  color: #4c4c4c;
  background: #fff;
  border-radius: 3px;
  margin: 1% 1.5%;
  position: relative;
  overflow: hidden;
}

.activity {
  color: #dc7d77;
  border: 1px solid #dc7d77;
  background: #fef4f3;
}

.beizhu {
  color: #878787;
  width: 100%;
  padding-bottom: 10px;
  font-size: 14px;
}

.man {
  background-image: url(data:image/png;base64,);
  background-repeat: no-repeat;
  background-position: top right;
  background-size: 30% 80%;
}

.t-footer{
  width: 100%;
  text-align: center;
}
  .t-btn{
    width: 90%;
    height: 40px;
    line-height: 40px;
    background: #f34e55;
    color: #fff;
    border: none;
    border-radius: 4px;
    font-size: 16px;
  }
</style>
