<template>
  <div
    class="area">
    <div
      class="city1 flex">
      <p
        class="title">
        所在城市
      </p>
      <p
        :class="{right1:wrong2}"
        type="text"
        class="content"
        readonly="readonly"
        @click="selectArea">{{ area.address }}
      </p>
    </div>
    <div
      class="detail flex">
      <p
        class="title">
        详细地址
      </p>
      <p
        :class="{right1:wrong2}"
        class="content"
        @click="focus">
        {{ detail }}
      </p>
    </div>
  </div>
</template>
<script>

import apiUrl from '../../common/js/config.api'
export default {

  directives: {
    focus: {
      inserted: function (el) {
        el.focus()
      }
    }
  },
  props: {
    areadata: {
      default () {
        return {}
      },
      type: Object
    }
  },
  data () {
    return {
      area: {
        cityId: 0,
        proId: 1,
        disId: 0,
        province: '',
        city: '',
        distri: '',
        address: '',
        detail: ''
      },
      isShow1: true,
      isShowPopup: false,
      detail: '',
      num: 0,
      isLocation: true
    }
  },
  watch: {
    areadata (val) {
      if (this.area.address !== val.address) {
        this.area.address = val.address
        let city = val.show1 && this.num === 0 ? this.area.address.split(' ')[1] : val.address.split(' ')[1]
        let dist = val.show1 && this.num === 0 ? this.area.address.split(' ')[2] : val.address.split(' ')[2]
        this.isLocation = false
        fetch(`${apiUrl.getByName}?cityName=${city}&distName=${dist}`).then(data => {
          return data.json()
        }).then(res => {
          this.area.cityId = res.data.dist.city.id
          this.area.distId = res.data.dist.id
          MIP.setData({
            orderData: {
              'cityId': this.area.cityId,
              'distId': this.area.distId,
              'address': this.area.address,
              'detail': this.detail
            },
            areaData: {
              'detail': this.detail,
              'address': this.area.address,
              'locationAddress': this.area.address
            }
          })
        })
      }
      this.area.address = val.show1 && this.num === 0 ? this.area.address : val.address
      this.detail = val.detail
      if (val.show1) this.num++
    }
  },
  mounted () {
    this.$on('success', res => {
      this.isLocation = false
      this.detail = res.address.city + ' ' + res.address.district + ' ' + res.address.street
      this.area.city = res.address.city
      this.area.address = res.address.province + ' ' + res.address.city + ' ' + res.address.district
      MIP.setData({
        chooseDetail: {
          'around': [],
          'city': res.address.city,
          'nowPosition': res.address.city + ' ' + res.address.district + ' ' + res.address.street + ' ' + res.address.street_number
        }
      })
      fetch(`${apiUrl.getByName}?cityName=${res.address.city}&distName=${res.address.district}`).then(data => {
        return data.json()
      }).then(res1 => {
        this.area.cityId = res1.data.dist.city.id
        this.area.distId = res1.data.dist.id
        MIP.setData({
          orderData: {
            'cityId': this.area.cityId,
            'distId': this.area.distId,
            'address': this.area.address,
            'detail': this.detail,
            'lat': res.point.lat,
            'lng': res.point.lng
          },
          areaData: {
            'detail': this.detail,
            'address': this.area.address,
            'locationAddress': this.area.address
          }
        })
      })
    })
    this.$on('failed', res => {
      console.log(res)
      this.isLocation = true
    })
  },
  methods: {
    selectArea () {
      MIP.setData({
        areaData: {
          'show': true
        }
      })
    },
    focus () {
      // 没有授权获取位置
      if (this.isLocation) {
        MIP.setData({
          alertMsg: {
            errorTitle: '您没有授权获取位置信息，请您先选择所在城市',
            isError: true
          }
        })
      } else {
        MIP.setData({
          chooseDetail: {
            'show': true
          },
          orderData: {
            'showFixed': true
          }
        })
      }
    },
    blur () {
      MIP.setData({
        orderData: {
          'detail': this.area.detail
        }
      })
    }
  }
}
</script>
